Random r = new Random();
int z = 0;
for(int i = 1; i < satz.length; i++)
{
	if(satz[i].equals("zwischen") || satz[i].equals("von"))
	{
		int untereGr = 0;
		int obereGr = 0;
		int j = 0;
		for(int k = i + 1; k < satz.length; k++)
		{
			try
			{
				untereGr = Integer.parseInt(satz[k]);
				j = k;
				break;
			} catch (NumberFormatException n1) {}


		}
		for(int t = j + 1; t < satz.length; t++)
		{
			try
			{
				obereGr = Integer.parseInt(satz[t]);
				break;
			} catch (NumberFormatException n2) {}
		}
		z = r.nextInt(obereGr-untereGr) + untereGr;
		break;
	} 
}
System.out.println(z);
